const output = [];

for (const html of items[0].json.postHtml) {
  const linkMatch = html.match(/<h2[^>]*>\s*<a href="([^"]+)"[^>]*>(.*?)<\/a>/i);
  const dateMatch = html.match(/<time[^>]+datetime="([^"]+)"/i);

  const tagsBlockMatch = html.match(/<div class="taxonomy-category wp-block-post-terms">([\s\S]*?)<\/div>/i);
  const tags = [];
  if (tagsBlockMatch) {
    const tagMatches = tagsBlockMatch[1].match(/<a[^>]+>([^<]+)<\/a>/g);
    if (tagMatches) {
      for (const tagAnchor of tagMatches) {
        const tagText = tagAnchor.replace(/<[^>]*>/g, "").trim();
        tags.push(tagText);
      }
    }
  }

  if (linkMatch && dateMatch) {
    const rawDate = new Date(dateMatch[1]);

    output.push({
      json: {
        title: linkMatch[2].trim(),
        url: linkMatch[1],
        date: rawDate.toLocaleString("en-US", {
          timeZone: "America/New_York",
          year: "numeric",
          month: "long",
          day: "2-digit",
          hour: "2-digit",
          minute: "2-digit",
          hour12: true,
        }),
        date_posted_et: rawDate.toISOString(), // ‚úÖ proper timestamp
        tags: tags
      }
    });
  }
}

return output;

