const items = $input.all();

// Pick earliest date_posted_et to anchor the "Week Of"
const earliestDate = items
  .map(i => new Date(i.json.date_posted_et))
  .sort((a, b) => a - b)[0];

const weekOf = earliestDate.toLocaleDateString("en-US", {
  month: "short",
  day: "2-digit",
  year: "numeric"
});

// Return a single item with the combined structure
return [{
  json: {
    week_of: weekOf,
    executive_orders: items.map(i => i.json)
  }
}];
